from flask import Flask, render_template,request, redirect
app = Flask(__name__)

data = [
        {"hang":"honda",
          "fee":"50000",
          "link":"data:image/jpeg;base64,",
          "year":"2010"},
        {"hang":"bmw",
          "fee":"500000",
          "link":"https://anh.24h.com.vn/upload/2-2017/images/2017-05-04/1493910006-1493869076303-1.jpg",
          "year":"2011"},
        {"hang":"harley",
          "fee":"1000000",
          "link":"https://www.harley-davidson.com/content/dam/h-d/images/motorcycles/my19/street/street-750/overview/hdi/19-street-street-750-hdi-hero.jpg",
          "year":"2015"},
        ] 
@app.route('/list_motor')
def result():
  return render_template("return_data.html", data = data)

@app.route('/motor', methods = ['GET', 'POST'])
def index():
    if request.method == 'GET':
        return render_template("motor.html")
    
    elif request.method == 'POST':
        form = request.form
        new_motor = {"hang":form["hang"],
        "fee":form["fee"],
        "link":form["link"],
        "year":form["year"],
        }
        data.append(new_motor)
        print(data)
        return redirect("/list_motor")

if __name__ == '__main__':
  app.run(debug=True)
 